import React, { useContext } from 'react';
import authContext from '../Context/authContext';

import './LogInBox.css';
const formSubmit =(event) =>{
  event.preventDefault();
}

const loginBox = (props) =>{
    const authContext = userContext(authContext);


    return(
      <div className="login-box">
        <h1>Inventory</h1>

        <form onSubmit={formSubmit}>
            <div className="login-cred">
                <input onChange={props.changeU} type="text" placeholder="Username"/>
                <input onChange={props.changeP} type="password" placeholder="Password"/>
            </div>
            <div>
                <input onClick={props.click} type="submit" className="login-button" value="LogIn"/>
            </div>
        </form>

    
        <div className = "sign-up">
          <p>Don't Have an account? <a href="../../public/index.html">Sign Up</a></p>
          <p>Forgot Password? <a href="../../public/index.html">Reset Password</a></p>
        </div>

      </div>
    );
}

export default loginBox;
