import React, { Component } from 'react';
//import React  from 'react';
//import LoginBox from '../Components/LoginBox';
import  './App.css'

class App extends Component {
  state = {
      userName: "",
      passWord: "",
      loginSuccesful: false
  }


  updateUser = (event) =>{
    this.setState(
      {userName: event.target.value}
    );

  }

  updatePass = (event) =>{
    this.setState({
      logInInfo:[
        {userName: this.state.logInInfo[0].userName,
        passWord: event.target.value,
        loginSuccesful: false}
      ]
    });

  }

  checkCred = () =>{
    if(this.state.logInInfo[0].userName === "admin" && this.state.logInInfo[0].passWord === "password")
    {
      this.setState(
        {
        logInInfo:[
          {userName: "",
          passWord: "",
          loginSuccesful: !this.state.logInInfo[0].loginSuccesful}
        ]

      }
      )
    }
  }
  render(){
    let cred = (

      <div className="login-box">
      <h1>Inventory</h1>
      <form>
          <div className="login-cred">
              <input onChange={this.updateUser} type="text" className="username" placeholder="Username" required/>
              <input onChange={this.updatePass} type="password" className="password" placeholder="Password"required/>
          </div>
          <div>
              <input onClick={this.checkCred} type="submit" className="login-button" value="Logout"/>
          </div>
      </form>
      <div className="sign-up">
          <p>Don't Have an account? Sign Up</p>
          <p>Forgot Password? Reset Password</p>
      </div>
  </div>

    );
    console.log(this.state.logInInfo[0].passWord);
    if(!this.state.logInInfo[0].loginSuccesful)
    {
      cred = (

        <div className="login-box">
            <h1>Inventory</h1>
            <form>
                <div className="login-cred">
                    <input onChange={this.updateUser} type="text" className="username" placeholder="Username" required/>
                    <input onChange={this.updatePass} type="password" className="password" placeholder="Password"required/>
                </div>
                <div>
                    <input onClick={this.checkCred} type="submit" className="login-button" value="LogIn"/>
                </div>
            </form>
            <div className="sign-up">
                <p>Don't Have an account? Sign Up</p>
                <p>Forgot Password? Reset Password</p>
            </div>
        </div>

      )
    }

    return(
 <div>
      {cred}      
      </div>
    );
  }

}
export default App;
