import React, {setState, Fragment} from 'react';
import './LoggedIn.css';
import InventoryItem from '../Components/InventoryItem/InventoryItem';

const LoggedIn = (props) =>{
    let inventory=[
        {description:"Name of Board and Components" , id:0},
        {description:"Name of Board and Components" , id:1},
        {description:"Name of Board and Components" , id:2},
        {description:"Name of Board and Components" , id:3},
        {description:"Name of Board and Components" , id:4}
    ];

    let totalInventory = inventory.map(item =>{
        return <InventoryItem description={item.description} key={item.id}/>
    });
    
    let itemArea = (
        <ul className='itemArea'>
                <ul className='itemSelector'>
                    <ul onClick={() => {props.itemTab(0)}} className={props.tabs[0].classes}>All Inventory</ul>
                    <ul onClick={() => {props.itemTab(1)}} className={props.tabs[1].classes}>Search</ul>
                    <ul onClick={() => {props.itemTab(2)}} className={props.tabs[2].classes}>Item</ul>
                    <ul onClick={() =>{props.logOut(true)}} className='itemTab'>Logout</ul>
                </ul>

                {totalInventory}

                
            </ul>
    );

    if(props.tabs[1].classes.includes("selectedItem"))
    {
        itemArea = (
            <ul className='itemArea'>
                    <ul className='itemSelector'>
                        <ul onClick={() => {props.itemTab(0)}} className={props.tabs[0].classes}>All Inventory</ul>
                        <ul onClick={() => {props.itemTab(1)}} className={props.tabs[1].classes}>Search</ul>
                        <ul onClick={() => {props.itemTab(2)}} className={props.tabs[2].classes}>Item</ul>
                        <ul onClick={() =>{props.logOut(true)}} className='itemTab'>Logout</ul>
                    </ul>
                        <input onChange={props.updateSearch} className='searchBar' type="text" value={props.searchBar}></input>
                     </ul>
        ); 
    }

    return(
        <Fragment>
            <ul className="header">
                <li className="headerItem" >Inventory</li>
                <li className="headerItem" >Profile</li>
            </ul>

            {itemArea}
    
        </Fragment>
    );
}

export default LoggedIn;
