import React, { PureComponent, Fragment } from 'react';
import AuthContext from '../Context/authContext';
import UserContext from '../Context/userContext'

import LoginBox from '../Components/LoginBox';
import LoggedIn from './LoggedIn';

import './App.css'
const debug = true;

class App extends PureComponent{
  state = {
    userCredentials:[username= "", password= ""],
      tabs: [
        {name: "All Inventory", classes: "itemTab selectedItem"},
        {name: "Search", classes: "itemTab"},
        {name: "Item", classes: "itemTab"}
      ],
      authenticated: false,
      currentSearch:''
  }

  updateCred = (event,credSelector) =>{
    index = (credSelector === 'username') ? 0 : 1;
    this.setState(userCredentials[index] = event.target.value);
  }

  updateSelected = (index) =>{
    let tabs = [
      {name: "All Inventory", classes: "itemTab"},
      {name: "Search", classes: "itemTab"},
      {name: "Item", classes: "itemTab"}
    ];
    let selectedTab = {...tabs[index]};
    selectedTab.classes += (" selectedItem");

    tabs[index] = selectedTab;

    this.setState({tabs:tabs});
  }

  updateSearch = (event) =>{
    this.setState(
      {currentSearch: event.target.value}
    );
  }

  render(){
    let item = null
    if(this.state.authenticated)
      item = (
      <UserContext value = {
        {logOut : this.CheckCred,
          itemTab : this.updateSelected,
          updateSearch : this.updateSearch,
          tabs : this.state.tabs,
          searchBar : this.state.currentSearch}}>

            <LoggedIn/>

      </UserContext>);

    else     
      item = (
        <AuthContext value = {
          {authenticated : this.state.authenticated,
          updateCred : this.updateCred,
          submit : this.submit}}>

            <LoginBox/> 

        </AuthContext>);

    return(
      <Fragment>
        {item}
      </Fragment>
    );
  }
}
export default App;
