import React from 'react';
import './LogInBox.css';

const loginBox = (props) =>{

  let form= null;

if(props.verified)
{
    form=(
        <form>
        <div className="login-cred">
            <input type="text" value="" required/>
        </div>
        <div>
            <input  onClick={() => {props.click}} className="login-button" value="Logout"/>
        </div>
    </form>
    );
}


    return(
      <div className="login-box">
        <h1>Inventory</h1>

        <form>
            <div className="login-cred">
                <input onChange={props.changeU} type="text" placeholder="Username" required/>
                <input onChange={props.changeP} type="password" placeholder="Password" required/>
            </div>
            <div>
                <input onClick={props.click} type="submit" className="login-button" value="LogIn"/>
            </div>
        </form>

    
        <div className = "sign-up">
          <p>Don't Have an account? <a href="#">Sign Up</a></p>
          <p>Forgot Password? <a href="#">Reset Password</a></p>
        </div>

      </div>
    );
}

export default loginBox;
