import React, { Component } from 'react';
import LoginForm from '../Components/LoginBox/LoginBox';
import  './App.css'

class App extends Component {
  state = {
      userName: "",
      passWord: "",
      verified: false
  }


  updateUser = (event) =>{
    this.setState(
      {userName: event.target.value}
    );

  }

  updatePass = (event) =>{
    this.setState( {passWord: event.target.value});

  }

  checkCred = (logout) =>{
    if((this.state.userName === "admin" && this.state.passWord === "password") || (logout === true))
    {
      const temp = this.state.verified;
      this.setState({verified:!temp})
      this.setState(
        {passWord: "",
      userName: ""}
      );
    }
  }
  render(){
    return(
      <div>
        <LoginForm></LoginForm>   
      </div>
    );
  }

}
export default App;
