import React, { Component } from 'react';

import LoginBox from '../Components/LoginBox';
import LoggedIn from './LoggedIn';

import './App.css'

class App extends Component {
  state = {
      userName: "",
      passWord: "",
      verified: false,
      tabs: [
        {name: "All Inventory", classes: "itemTab selectedItem"},
        {name: "Search", classes: "itemTab"},
        {name: "Item", classes: "itemTab"}
      ],
      currentSearch:''
  }

  updateUser = (event) => {
    this.setState({userName: event.target.value})

  }
  updatePass = (event) => {
    this.setState({passWord: event.target.value});

  }

  updateSelected = (index) =>{
    let tabs = [
      {name: "All Inventory", classes: "itemTab"},
      {name: "Search", classes: "itemTab"},
      {name: "Item", classes: "itemTab"}
    ];

    let selectedTab = {...tabs[index]};
    selectedTab.classes += (" selectedItem");


    tabs[index] = selectedTab;

    this.setState(
      {tabs:tabs}
    );

  }

  updateSearch = (event) =>{
    this.setState(
      {currentSearch: event.target.value}
    );
  }

  checkCred = (logout) =>{

    var xhr = new XMLHttpRequest()

    // get a callback when the server responds
    xhr.addEventListener('load', () => {
      // update the state of the component with the result here
      console.log(xhr.responseText)
    })
    // open the request with the verb and the url
    let user = this.state.userName
    let pass = this.state.passWord
    let url = window.location.href
    xhr.open('GET', url+'/user?userName='+user + '&passWord=' + pass)
    // send the request
    xhr.send()
    console.log(url+'/user?userName='+user + '&passWord=' + pass);
    if(xhr.responseText == 'true')
    {
      const temp = this.state.verified;
      this.setState({verified:!temp})
      this.setState({passWord: "", userName: "",currentSearch:''});

      this.updateSelected(0);
    }

  }

  render(){

    let item = null
    if(this.state.verified)
      item = (<LoggedIn
      logOut = {this.checkCred}
      itemTab = {this.updateSelected}
      tabs = {this.state.tabs}
      updateSearch = {this.updateSearch}
      searchBar = {this.state.currentSearch}
      />);
    else
      item = (<LoginBox
      changeU = {this.updateUser}
      changeP = {this.updatePass}
      click = {this.checkCred}
      verified = {this.state.verified}
      /> );

    return(
      <div>
        {item}
      </div>
    );
  }
}
export default App;
