import React, { Component } from 'react';
//import React  from 'react';
//import LoginBox from '../Components/LoginBox';
import  './App.css'

class App extends Component {
  state = {
      userName: "",
      passWord: "",
      verified: false
  }


  updateUser = (event) =>{
    this.setState(
      {userName: event.target.value}
    );

  }

  updatePass = (event) =>{
    this.setState( {passWord: event.target.value});

  }

  checkCred = (logout) =>{
    if((this.state.userName === "admin" && this.state.passWord === "password") || (logout === true))
    {
      const temp = this.state.verified;
      this.setState({verified:!temp})
      this.setState(
        {passWord: "",
      userName: ""}
      );
    }
  }
  render(){
    let screen = ( 
      <div className="login-box">
            <h1>Inventory</h1>
            <div className="login-cred">
                    <input onChange={this.updateUser} type="text" className="username" placeholder="Welcome Admin" required/>
             </div> 
             <div>
                    <input onClick={() =>{this.checkCred(true)}} type="submit" className="login-button" value="Logout"/>
              </div>      
        </div>

    );
    if(!this.state.verified)
    {
      screen = (

        <div className="login-box">
            <h1>Inventory</h1>
            <form>
                <div className="login-cred">
                    <input onChange={this.updateUser} type="text" className="username" placeholder="Username" required/>
                    <input onChange={this.updatePass} type="password" className="password" placeholder="Password"required/>
                </div>
                <div>
                    <input onClick={this.checkCred} type="submit" className="login-button" value="LogIn"/>
                </div>
            </form>
            <div className="sign-up">
                <p>Don't Have an account? <a href="#">Sign Up</a></p>
                <p>Forgot Password? Reset Password</p>
            </div>
        </div>

      )
    }

    return(
      <div>
        {screen}      
      </div>
    );
  }

}
export default App;
