import React, { Component } from 'react';

import LoginBox from '../Components/LoginBox';
import LoggedIn from './LoggedIn';

import './App.css'

class App extends Component {
  state = {
      userName: "",
      passWord: "",
      verified: true,
      tabs: [
        {name: "All Inventory", classes: "itemTab selectedItem"},
        {name: "Search", classes: "itemTab"},
      ]
  }

  updateUser = (event) => this.setState({userName: event.target.value})

  updatePass = (event) => this.setState({passWord: event.target.value});

  updateSelected = (index) =>{




      let tabs = [
        {name: "All Inventory", classes: "itemTab"},
        {name: "Search", classes: "itemTab"},
      ];




    let selectedTab = {...tabs[index]};
    selectedTab.classes.concat(" selectedItem");


    tabs[index] = selectedTab;


    this.setState(
      {tabs:tabs}
    );
  }

  checkCred = (logout) =>{
    if((this.state.userName === "admin" && this.state.passWord === "password") || (logout === true))
    {
      const temp = this.state.verified;
      this.setState({verified:!temp})
      this.setState({passWord: "", userName: ""});
    }
  }

  render(){

    let item = null
    if(this.state.verified)
      item = (<LoggedIn
      logOut = {this.checkCred}
      itemTab = {this.updateSelected}
      tabs = {this.state.tabs}
      />);
    else
      item = (<LoginBox
      changeU = {this.updateUser}
      changeP = {this.updatePass}
      click = {this.checkCred}
      verified = {this.state.verified}
      /> );

    return(
      <div>
        {item}
      </div>
    );
  }
}
export default App;
