import React from 'react';
import './LoginForm.css';

const loginForm = (props) =>{
    let form = null;
    if(!props.verified)
    {
        form=(
            <form>
            <div className="login-cred">
                <input onChange={props.changeU} type="text" placeholder="Username" required/>
                <input onChange={props.changP} type="password" placeholder="Password"required/>
            </div>
            <div>
                <input onClick={props.click} type="submit" className="login-button" value="LogIn"/>
            </div>
        </form>
        );
    }
    else
    {
        form=(
            <form>
            <div className="login-cred">
                <input onChange={props.changeU} type="text" placeholder="Welcome Admin" required/>
            </div>
            <div>
                <input onClick={props.click} type="submit" className="login-button" value="Logout"/>
            </div>
        </form>
        );
    }

    return(
        <div>{form}</div>
        
    );
}

export default loginForm;
