import React, { Component } from 'react';
//import React  from 'react';
import LoginBox from '../Components/LoginBox';
import  './App.css'

class App extends Component {
  state = {
    logInInfo:[
      {userName: " ",
      passWord: " ",
      loginSuccesful: false}
    ]
  }


  updateUser = (event) =>{
    this.setState({
      logInInfo:[{userName: event.target.value,}]
    });
  }

  updatePass = (event) =>{
    this.setState({
      logInInfo:[{passWord: event.target.value,}]
    });
    console.log(this.state.passWord);
  }

  checkCred = () =>{
    if(this.state.useName === "admin" && this.state.passWord === "password")
    {
      this.setState(
        {logInInfo:[{loginSuccesful: true}]}
      )

      console.log("Changed to True");
    }

    console.log("Entered Handler");
  }
  render(){
    let cred = (
      <div></div>
    );

    if(!this.state.loginSuccesful)
    {
      cred = (
        <body>
        <div className="login-box">
            <a><h1>Inventory</h1></a>
            <form>
                <div className="login-cred">
                    <input onChange={this.updateUser} type="text" className="username" placeholder="Username" required/>
                    <input onChange={this.updatePass} type="password" className="password" placeholder="Password"required/>
                </div>
                <div>
                    <input onClick={this.checkCred}  className="login-button" value="LogIn"/>
                </div>
            </form>
            <div className="sign-up">
                <p>Don't Have an account? <a href="">Sign Up</a></p>
                <p>Forgot Password? <a href="">Reset Password</a></p>
            </div>
        </div>
    </body>
      )
    }

    return(
    <html>
      {cred}      
    </html>
    );
  }

}
export default App;
