import React, {useState, Fragment} from 'react';
import './LoggedIn.css';
import InventoryItem from '../Components/InventoryItem/InventoryItem';



const LoggedIn = (props) =>{
    const [currentSearch, setSearch] = useState("");
    
    let inventory=[
        {description:"Name of Board and Components" , id:1},
        {description:"Name of Board and Components" , id:2},
        {description:"Name of Board and Components" , id:3},
        {description:"Name of Board and Components" , id:4},
        {description:"Name of Board and Components" , id:13}
    ];
    
    
    const updateSearch = (event) =>{
        setSearch(event.target.value);   
    } 

    
    let selectedInventory = inventory.map(item =>{
        if(currentSearch == (item.id))
        {
            return <InventoryItem description={item.description} id={item.id} key={item.id}/>;
        }
        else    
        {
            return null;
        }
    });
    

    let totalInventory = inventory.map(item =>{
        return <InventoryItem description={item.description} id={item.id} key={item.id}/>
    });

    




    let itemSelector=(
        <ul className='itemSelector'>
        <ul onClick={() => {props.itemTab(0)}} className={props.tabs[0].classes}>All Inventory</ul>
        <ul onClick={() => {props.itemTab(1)}} className={props.tabs[1].classes}>Search</ul>
        <ul onClick={() => {props.itemTab(2)}} className={props.tabs[2].classes}>Item</ul>
        <ul onClick={() =>{props.logOut(true)}} className='itemTab'>Logout</ul>
    </ul>
    );
    
    let itemArea = null;
    if(props.tabs[0].classes.includes("selectedItem"))
    {
        itemArea = (
            <ul className='itemArea'>
                 {itemSelector}
                {totalInventory}    
            </ul>
        ); 
    }

    else if(props.tabs[1].classes.includes("selectedItem"))
    {
        itemArea = (
            <ul className='itemArea'>
                {itemSelector}
                <input onChange={updateSearch} className='searchBar' type="text" value={currentSearch}></input>
                {selectedInventory}
            </ul>
        ); 
    }

    else if(props.tabs[2].classes.includes("selectedItem"))
    {
        itemArea = (
            <ul className='itemArea'>
                {itemSelector}
                
            </ul>
        ); 
    }

    return(
        <Fragment>
            <ul className="header">
                <li className="headerTitle" >Inventory</li>
                <li className="headerProfile" >Profile</li>
            </ul>

            {itemArea}
    
        </Fragment>
    );
}

export default LoggedIn;
